import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

function loadImages(){
        // Declare an array object for our array of images
        let images = [];
        var imageFiles = ['bull1.jpg','bull2.jpg','bull3.jpg','bull4.jpg',
                        'bull5.jpg','bull6.jpg','bull7.jpg','bull8.jpg']  

        for (var i = 0, j = imageFiles.length; i < j; i++) {
        var image = new Image; //This is a placeholder
        image.src = 'bulldog/' + imageFiles[i]; //Set the src attribute
        images.push(image); //Append the new image into the pictures array
        }

        //Show the result:
        console.log(images);
        return images;
}

function shuffle (arr) {
    var j, x, index;
    for (index = arr.length - 1; index > 0; index--) {
        j = Math.floor(Math.random() * (index + 1));
        x = arr[index];
        arr[index] = arr[j];
        arr[j] = x;
    }
    return arr;
}

function Square(props){
  return (
    <button
      className="4square"
      onClick={()=> props.onClick()}>
      {props.value}
    </button>
  );
}

function Image(props){
  console.log("source: ", props.source)
  return (
    <img className="libimages" src={props.source} alt="library" 
width="200" height="200"/>
  )
}

class Board extends React.Component {
  constructor(props) {
    super(props);  
    let value_range = [];
    for (var i = 0; i <= 7; i++) {
      value_range.push(i);
    }
    let value_range_s1 = shuffle(value_range);
    let value_range_s2 = shuffle(value_range_s1);
    this.state = {   
      square_show: Array(16).fill(null),
      square_matched: Array(16).fill(null),
      //square_value: [1,2,3,4,5,6,7,8,8,7,6,5,4,3,2,1],
      square_value: value_range_s1.concat(value_range_s2),
      first_pick: true,
      first_pick_index: -1,
      score: 0,
      playable: 1,
    };
  }

 handleClick(i) {
    const square_show = this.state.square_show.slice();
    const square_matched = this.state.square_matched.slice();
    const square_value = this.state.square_value.slice();
    if (this.state.playable==0 || calculateWinner(square_matched) ||
square_matched[i]){
      return;
    }
    if (square_show.includes(square_value[i]) && i!=this.state.first_pick_index){
      square_matched[i]=1;
      square_matched[this.state.first_pick_index]=1;
      square_show[i] = square_value[i];
      this.setState({
        square_show: square_show,
        square_matched: square_matched,
        first_pick: true,
        first_pick_index: -1,
        score: this.state.score + 1,
      });
    }else{
      if (this.state.first_pick==true){
        square_show[i] = square_value[i];
        this.setState({
          square_show: square_show,
          first_pick: false,
          first_pick_index: i,
          score: this.state.score + 1,
        });
      }else{
        square_show[i] = square_value[i];
        this.setState({
          playable: 0,
          square_show: square_show,
          score: this.state.score + 1,
        });
        setTimeout(function(){
          square_show[this.state.first_pick_index] = null;
          square_show[i] = null; 
          this.setState({
            playable: 1, 
            square_show: square_show,
            first_pick: true,
            first_pick_index: -1,
          });
        }.bind(this), 1500);
      }
    }
  }

  renderSquare(i) {
    return (
      <Square
        value={this.state.square_show[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }

  renderImage(i) {
    var imgname = "bulldog/bull" + i + ".png";
    return (
      <Image      
        source={imgname}
	onClick={() => this.handleClick(i)}
      />
    );
  }

  render() {
    const winner = calculateWinner(this.state.square_matched);
    let status;
    if (winner) {
      status = 'Winner! Score:' + this.state.score;
    } else{
      status = 'Choose a Card. Score: ' + this.state.score;
    }

    var ar = [0,1,2,3];
    return (
      <div className="rows">
        <div className="img-row">
          <div
className="lib-img">{this.renderImage(this.state.square_show[0])}</div>
          <div
className="lib-img">{this.renderImage(2)}</div>
          <div
className="lib-img">{this.renderImage(3)}</div>
        </div>
        <div className="img-row">
          <div
className="lib-img">{this.renderImage(4)}</div>
          <div
className="lib-img">{this.renderImage(5)}</div>
          <div
className="lib-img">{this.renderImage(6)}</div>
        </div>
      </div>
    );    
  }
}


class Game extends React.Component {
  render() {   
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

