import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Square(props){
  return (
    <button
      className="square" 
      onClick={()=> props.onClick()}>
      {props.value}
    </button>
  );
}

class Board extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      first_choice: true,
      first_choice_index: -1,
      square_show: Array(16).fill(null),
      matched_sqaure: Array(16).fill(null),
      square_value: [0,1,2,3,4,5,6,7,7,6,5,4,3,2,1,0],
    };
  }

  handleClick(i) {
    const square_show = this.state.squares.slice();
    const matched_square = this.state.matched_square.slice();
    if (calculateWinner(squares) || matched_square[i]){
      return;
    }
    if (square_show.includes(square_value[i])){
      square_show[i] = square_value[i];
      matched_square[i] = 1;
      matched_square[this.first_choice_index] = 1;
      this.setState({
        square_show: square_show,
        matched_square: matched_square,
      });
    }else{
      if (first_choice==true){
        square_show[i] = square_value[i];
        this.setState({
          square_show: square_show,
          first_choice: !this.state.first_choice,
          first_choice_index: i,
        });
      }
      else{
        this.setState({
          first_choice: !this.state.first_choice,
          first_choice_index: -1, 
        });
      }
    }
  }

  renderSquare(i) {
    return (
      <Square
        value={this.state.show_square[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }

  render() {
    const winner = 
calculateWinner(this.state.matched_square);
    let status;
    if (winner) {
      status = 'Winner: ' + winner;
    } else{
      status = 'Pick a Card';
    }

    return (
      <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
          {this.renderSquare(3)}
        </div>
        <div className="board-row">
          {this.renderSquare(4)}
          {this.renderSquare(5)}
          {this.renderSquare(6)}
          {this.renderSquare(7)}
        </div>
        <div className="board-row">
          {this.renderSquare(8)}
          {this.renderSquare(9)}
          {this.renderSquare(10)}
          {this.renderSquare(11)}
        </div>
        <div className="board-row">
          {this.renderSquare(12)}
          {this.renderSquare(13)}
          {this.renderSquare(14)}
          {this.renderSquare(15)}
        </div>
      </div>
    );
  }
}

class Game extends React.Component {
  render() {
    return (
      <div className="game">
        <div className="game-board">
          <Board />
        </div>
        <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
        </div>
      </div>
    );
  }
}

// ========================================

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<Game />);

function calculateWinner(matched_square) {
  const win = 1;
  for (let i = 0; i < matched_square.length; i++) {
    if (matched_square[i] == 0){
      win = matched_square[i];
    }
  }
  return win;
}
