import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

function loadImages(){
        // Declare an array object for our array of images
        let images = [];
        var imageFiles = ['bull0.jpg', 'bull1.jpg','bull2.jpg','bull3.jpg','bull4.jpg',
                        'bull5.jpg','bull6.jpg','bull7.jpg','bull8.jpg']  

        for (var i = 0, j = imageFiles.length; i < j; i++) {
        var image = new Image; //This is a placeholder
        image.src = 'bulldog/' + imageFiles[i]; //Set the src attribute
        images.push(image); //Append the new image into the pictures array
        }

        //Show the result:
        console.log(images);
        return images;
}

//Fisher-Yates shuffle algorithm 
function shuffle(array) {
  let array_copy = array;
  let currentIndex = array.length,  randomIndex;

  // While there remain elements to shuffle.
  while (currentIndex != 0) {

    // Pick a remaining element.
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    // And swap it with the current element.
    [array[currentIndex], array[randomIndex]] = [
      array[randomIndex], array[currentIndex]];
  }
  return array;
}

function shuffle2(array) {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
    }
  return array;
}

function Square(props){
  return (
    <button
      className="4square"
      onClick={()=> props.onClick()}>
      {props.value}
    </button>
  );
}

function Image(props){
  var imgindex = props.source.charAt(props.source.length-5);
  //console.log("imgindex: ", imgindex);
  return (
    <button>
      <img className="libimages" src={props.source} alt="libraries" 
      width="100" height="100"
      onClick = {()=>props.onClick(imgindex)}/>
    </button>
  )
}

class Board extends React.Component {
  constructor(props) {
    super(props);  
    let vr1 = [];
    let vr2 = [];
    for (var i = 1; i <= 8; i++) {
      vr1.push(i);
      vr2.push(i);
    }
    let vr = vr1.concat(vr2);
    let vrs = shuffle(vrs);
    let vr1_s = shuffle(vr1);
    let vr2_s = shuffle(vr2);
    let pset;
    this.state = {   
      img_show: Array(16).fill(0),
      img_matched: Array(16).fill(0),
      //img_value: [1,2,3,4,5,6,7,8,8,7,6,5,4,3,2,1],
      //img_value: vr1_s.concat(vr2_s),
      img_value: vrs,
      first_pick: true,
      first_pick_index: -1,
      score: 0,
      playable: 1,
      pset: "bulldog",
    };
  }

  handleChange(e){
    console.log("pset value: ", e.target.value);
    this.setState({
      [e.target.id]: e.target.value
      //playable: 1,
      //pset: e.target.value,
    });
  }

  handleClick(i) {
    console.log("clicked i: ", i)
    const img_show = this.state.img_show.slice();
    const img_matched = this.state.img_matched.slice();
    const img_value = this.state.img_value.slice();
    if (this.state.playable==0 || calculateWinner(img_matched) || img_matched[i]){
      return;
    }
    if (img_show.includes(img_value[i]) && i!=this.state.first_pick_index){
      img_matched[i]=1;
      img_matched[this.state.first_pick_index]=1;
      img_show[i] = img_value[i];
      this.setState({
        img_show: img_show,
        img_matched: img_matched,
        first_pick: true,
        first_pick_index: -1,
        score: this.state.score + 1,
      });
    }else{
      if (this.state.first_pick==true){
        img_show[i] = img_value[i];
        this.setState({
          img_show: img_show,
          first_pick: false,
          first_pick_index: i,
          score: this.state.score + 1,
        });
      }else{
        img_show[i] = img_value[i];
        this.setState({
          playable: 0,
          img_show: img_show,
          score: this.state.score + 1,
        });
        setTimeout(function(){
          img_show[this.state.first_pick_index] = 0;
          img_show[i] = 0; 
          this.setState({
            playable: 1, 
            img_show: img_show,
            first_pick: true,
            first_pick_index: -1,
          });
        }.bind(this), 1500);
      }
    }
  }

  renderSquare(i) {
    return (
      <Square
        value={this.state.img_show[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }

  renderImage(i) {
    var index = this.state.img_show[i]
    if (this.state.pset == "bulldog"){
      var imgname = this.state.pset + "/bull" + index + ".jpg";
    }
    else if (this.state.pset == "chickens"){
      var imgname = this.state.pset + "/chicks" + index + ".jpg";
    }
    else if (this.state.pset == "puppies"){
      var imgname = this.state.pset + "/p" + index + ".jpg";
    }
    else if (this.state.pset == "sea"){
      var imgname = this.state.pset + "/s" + index + ".jpg";
    }
    return (
      <Image      
        source={imgname}
	onClick={() => this.handleClick(i)}
       />
    );
  }

  render() {
    const winner = calculateWinner(this.state.img_matched);
    let status;
    let imgarg;
    if (winner) {
      status = 'Winner! Score: ' + this.state.score;
    } else{
      status = 'Choose a Card. Score: ' + this.state.score;
    }

    var ar = [0,1,2,3];
    var message = 'You selected the ' + this.state.pset + ' photoset.';
    return (
      <div>
        <label>
          Choose your photoset:
          <select 
            value={this.state.pset}
            onChange={this.handleChange}
          >
            <option value="Bulldogs">bulldog</option>
            <option value="Puppies">puppies</option>
            <option value="Baby Chickens">chickens</option>
            <option value="Sea Life">sea</option>
          </select>
        <p>{message}</p>
        </label>
      <div className="status">{status}</div>
      <div className="rows">
        <div className="img-row">
          <div
