function shuffle(array) {
  let currentIndex = array.length,  randomIndex;

  // While there remain elements to shuffle.
  while (currentIndex != 0) {

    // Pick a remaining element.
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    // And swap it with the current element.
    [array[currentIndex], array[randomIndex]] = [
      array[randomIndex], array[currentIndex]];
  }
  console.log("FY shuf: ", array)
  console.log("FY shuf: ", array[0])
  console.log("FY shuf: ", array[1])
  console.log("FY shuf: ", array[2])
  return array;
}

function shuffleArray(array) {
    for (let i = array.length - 1; i > 0; i--) {
        const j = Math.floor(Math.random() * (i + 1));
        [array[i], array[j]] = [array[j], array[i]];
    }
  console.log("D shuf: ", array)
  console.log("D shuf: ", array[0])
  console.log("D shuf: ", array[1])
  console.log("D shuf: ", array[2])
  return array;
}

