      var imgname = dir + "/chicks" + this.state.img_show[i] + ".jpg";
    }
    else if (dir == "puppies"){
      var imgname = dir + "/p" + this.state.img_show[i] + ".jpg";
    }
    else if (dir == "sea"){
      var imgname = dir + "/s" + this.state.img_show[i] + ".jpg";
    }
    console.log("imgname: ", imgname)
    return (
      <Image      
        source={imgname}
	onClick={() => this.handleClick(i)}
       />
    );
  }

  render() {
    const winner = calculateWinner(this.state.img_matched);
    let status;
    let pset;
    if (winner) {
      status = 'Winner! Score: ' + this.state.score;
    } else{
      status = 'Choose a Card. Score: ' + this.state.score;
    }

