            <option pset="Bulldogs">bulldog</option>
            <option pset = "Sea Life">sea</option>
          </select>
        </label>
	console.log("pset: ", pset)
      <div className="status">{status}</div>
      <div className="rows">
        <div className="img-row">
          <div
className="lib-img">{this.renderImage(pset,0)}</div>
