import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

function Square(props){
  return (
    <button
      className="options" 
      onClick={()=> props.onClick()}>
      {props.value}
    </button>
  );
}

function Image(props){
  console.log("source: ", props.source)
  return (
    <img className="libimages" src={props.source} alt="library" 
width="200" height="200"/>
  )
}

function loadImages(){
        // Declare an array object for our array of images
        let images = [];
        var imageFiles = ['bull1.jpg','bull2.jpg','bull3.jpg','bull4.jpg',
                        'bull5.jpg','bull6.jpg','bull7.jpg','bull8.jpg']  

        for (var i = 0, j = imageFiles.length; i < j; i++) {
        var image = new Image; //This is a placeholder
        image.src = 'bulldog/' + imageFiles[i]; //Set the src attribute
        images.push(image); //Append the new image into the pictures array
        }

        //Show the result:
        console.log(images);
        return images;
}

function handleClick(i) {
  return;
}

function renderSquare(i) {
  return (
    <Square
      value={i}
      onClick={() => this.handleClick(i)}
    />
  );
}

function renderImage(i) {
  return (
    <Image      
      source={i}
    />
  );
}

function Library() {
  return (
      <div className="rows">
        <div className="img-row">
          <div 
className="lib-img">{renderImage("bulldog/bull1.jpg")}</div>
          <div 
className="lib-img">{renderImage("bulldog/bull2.jpg")}</div>
          <div 
className="lib-img">{renderImage("bulldog/bull3.jpg")}</div>
        </div>
        <div className="img-row">
          <div 
className="lib-img">{renderImage("bulldog/bull4.jpg")}</div>
          <div 
className="lib-img">{renderImage("bulldog/bull5.JPG")}</div>
          <div 
className="lib-img">{renderImage("bulldog/bull6.jpg")}</div>
        </div>
        <div className="img-row">
          <div 
className="lib-img">{renderImage("bulldog/bull7.jpg")}</div>
          <div 
className="lib-img">{renderImage("bulldog/bull8.jpg")}</div>
          <div 
className="lib-img">{renderImage("bulldog/bull9.jpg")}</div>
        </div>
      </div>
  );
}

function renderLib(){
  return (
    <Library />
  );
}

class LPage extends React.Component {
  render() {
    return (
      <div className="library">
          {renderLib()}
      </div>
    );
  }
}

// ========================================

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<LPage />);
